select * from customer_data limit 20;

--1. What is the total revenue generated by male vs female customers?

select gender,sum(purchase_amount) as revenue
from customer_data
group by gender;

--2. Which customer used discount but still spent more than the average purchase amount?

select customer_id,purchase_amount
from customer_data
where discount_applied = 'Yes' and purchase_amount >= (select avg(purchase_amount)from customer_data);

--3. Which are the top 5 products with highest average review rating?

select item_purchased,avg(review_rating) as rating
from customer_data
group by item_purchased
order by rating desc
limit 5;

--4. Compare the average purchase amounts between standard and express shipping?

select shipping_type,avg(purchase_amount) as amount
from customer_data
where shipping_type in ('Standard','Express')
group by shipping_type
order by amount desc;

--5. Do subscribed customers spend more? Compare average spend and total revenue 
--between subscribed and non subscribed

select subscription_status,count(customer_id) as total_customers,
    avg(purchase_amount) as amount,sum(purchase_amount) as total
from customer_data
group by subscription_status
order by total,amount desc;

--6. Which product have the highest percentage of purchase with discount applied?

select item_purchased,round(100 *sum(case when discount_applied = 'Yes' then 1 else 0 end)/count(*),2) as discount_rate
from customer_data
GROUP BY item_purchased
order by discount_rate desc
limit 5;

--7. segment customer into new,returning and loyal?
with customer_type as (
    select customer_id,previous_purchases,
    case when previous_purchases = 1 then 'new'
    when previous_purchases between 2 and 10 then 'returning'
    else 'loyal'
    end as customer_segment
    from customer_data
)
select customer_segment,count(*) as "number of customers"
from customer_type
group by customer_type

--8. What are the top  purchased products within each category?

with item_counts as(
    select category,item_purchased,count(customer_id) as total_orders,
row_number() over(partition by category order by count(customer_id) desc) as item_rank
from customer_data
group by category,item_purchased
)
select item_rank,category,item_purchased,total_orders
from item_counts
where item_rank <= 3;

--9. Are customers who are repeat buyers also likely to subscribe?

select subscription_status,count(customer_id) as  repeat_buyers
from customer_data
where previous_purchases > 5
group by subscription_status

--10. what is the revenue contribution of each age group?

select age_group,sum(purchase_amount) as revenue
from customer_data
group by age_group
order by revenue desc
